import React, { useRef, useEffect } from 'react';
import useChatStore from '../store/useChatStore';
import Chatheader from './Chatheader';
import Messageinput from './Messageinput';
import MessageSkeleton from './skeletons/MessageSkeleton';
import useAuthStore from '../store/useAuthStore';

const ChatContainer = () => {
  const { messages, isMessagesLoading, getMessages, selectedUser, unSubscribeToMessage, subscribeToMessage } = useChatStore();
  const { authUser } = useAuthStore();
  const messageEndRef = useRef(null);

  useEffect(() => {
    getMessages(selectedUser._id);
    subscribeToMessage();

    return () => unSubscribeToMessage();
  }, [selectedUser._id, getMessages, unSubscribeToMessage, subscribeToMessage]);
  useEffect(() => {
    if (messageEndRef.current && messages) {
      messageEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [messages]);
  if (isMessagesLoading) return (
    <div className="flex flex-col flex-1 overflow-auto">
        <Chatheader/>
        <MessageSkeleton/>
    </div>
  );

  return (
    <div className='flex flex-1 flex-col overflow-hidden'>
        <Chatheader/>
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((message) => (
          <div
            key={message._id}
            className={`chat ${message.senderId === authUser._id ? "chat-end" : "chat-start"}`}
            ref={messageEndRef}
          >
            <div className=" chat-image avatar">
              <div className="size-10 rounded-full border">
                <img
                  src={
                    message.senderId === authUser._id
                      ? authUser.profilePic || "data:image/png;base64,"
                      : selectedUser.profilePic || "data:image/png;base64,"
                  }
                  alt="profile pic"
                />
              </div>
            </div>
            <div className="chat-header mb-1">
              <time className="text-xs opacity-50 ml-1">
                {/* {formatMessageTime(message.createdAt)} */}
                {message.createdAt}
              </time>
            </div>
            <div className="chat-bubble flex flex-col">
              {message.image && (
                <img
                  src={message.image}
                  alt="Attachment"
                  className="sm:max-w-[200px] rounded-md mb-2"
                />
              )}
              {message.text && <p>{message.text}</p>}
            </div>
          </div>
        ))}
      </div>
      <Messageinput/>
    </div>
  );
};

export default ChatContainer;
